mapper.select:
SELECT o.`id`,o.`createTime`,o.`cost`,o.`serialNumber`,ua.`consignee` AS consignee,ua.`address` AS address,ua.`addressPhone` AS addressPhone,ua.`postcode` AS postcode, dt.`timeName` AS timeName ,ps.`statusName` AS statusName,pw.`wayName` AS wayName,dw.`dbtName` AS dbtName, od.`productId` AS productId,od.`quantity` AS quantity,od.`cost` AS costt,pp.fileName AS fileName, pp.name AS `name`,pp.colour as colour,pp.price AS price,ds.`deliveryStatus`AS `deliveryStatus` FROM `order` o JOIN `user_address` ua ON o.userAddressId=ua.id JOIN `delivery_time` dt ON o.`deliveryTimeId`=dt.id JOIN `payment_status` ps ON o.`paymentStatusId`=ps.id JOIN `payment_way` pw ON o.`paymentWayId`=pw.id JOIN `distribution_way` dw ON o.`distributionWayId`=dw.id JOIN `order_detail` od ON o.id=od.`orderId` JOIN `product` pp ON pp.id=od.`productId` LEFT JOIN `delivery_status` ds ON ds.id=o.`deliveryStatusId` WHERE 1=1
and o.userId=#{uid}
and o.serialNumber=#{uNum}

SELECT o.`id`,o.`createTime`,o.`cost`,o.`serialNumber`,ua.`consignee` AS consignee,ua.`address` AS address,ua.`addressPhone` AS addressPhone,ua.`postcode` AS postcode, dt.`timeName` AS timeName ,ps.`statusName` AS statusName,pw.`wayName` AS wayName,dw.`dbtName` AS dbtName, od.`productId` AS productId,od.`cost` AS costt,pp.fileName AS fileName , pp.name AS `name`,pp.price AS price ,COUNT(o.serialNumber) AS suCount,ds.`deliveryStatus`AS `deliveryStatus` FROM `order` o JOIN `user_address` ua ON o.userAddressId=ua.id JOIN `delivery_time` dt ON o.`deliveryTimeId`=dt.id JOIN `payment_status` ps ON o.`paymentStatusId`=ps.id JOIN `payment_way` pw ON o.`paymentWayId`=pw.id JOIN `distribution_way` dw ON o.`distributionWayId`=dw.id JOIN `order_detail` od ON o.id=od.`orderId` JOIN `product` pp ON pp.id=od.`productId` LEFT JOIN `delivery_status` ds ON ds.id=o.`deliveryStatusId` WHERE 1=1
and o.userId=#{uid}
GROUP BY o.serialNumber HAVING COUNT(o.serialNumber) ORDER BY o.createTime DESC limit #{from},#{pageSize}
